<?phpinclude 'database_connection.php';check_login();if (empty($input['name'])) {    send_error('Company name is required.');}$name = $conn->real_escape_string($input['name']);$onHold = 0; // NEW: Default to 0 (false)$sql = "INSERT INTO companies (name, onHold) VALUES (?, ?)";$stmt = $conn->prepare($sql);$stmt->bind_param("si", $name, $onHold);if ($stmt->execute()) {    $newId = $conn->insert_id;    send_json([        'id' => $newId,        'name' => $input['name'],        'onHold' => false // Send back as boolean    ]);} else {    send_error('Error adding company: ' . $conn->error, 500);}$stmt->close();$conn->close();?>