<?phpinclude 'database_connection.php';check_login(); // <-- NEW: Security Checkif (empty($input['name'])) {    send_error('Addon name is required.');}$name = $conn->real_escape_string($input['name']);$sql = "INSERT INTO addons (name) VALUES ('$name')";if ($conn->query($sql) === TRUE) {    $newId = $conn->insert_id;    send_json([        'id' => $newId,        'name' => $input['name']    ]);} else {    send_error('Error adding addon: ' . $conn->error, 500);}$conn->close();?>