<?phpinclude 'database_connection.php';check_login(); // <-- NEW: Security Check// Validate inputif (empty($input['name']) || empty($input['email']) || empty($input['companyId']) || empty($input['licenseType']) || empty($input['licenseExpiry'])) {    send_error('Missing required fields.');}$name = $conn->real_escape_string($input['name']);$email = $conn->real_escape_string($input['email']);$companyId = (int)$input['companyId'];$licenseProduct = "EFD"; // Hardcoded$licenseType = $conn->real_escape_string($input['licenseType']);$licenseExpiry = $conn->real_escape_string($input['licenseExpiry']);// UPDATED: No more licenseProduct$sql = "INSERT INTO users (name, email, companyId, licenseType, licenseExpiry)         VALUES ('$name', '$email', $companyId, '$licenseType', '$licenseExpiry')";if ($conn->query($sql) === TRUE) {    $newId = $conn->insert_id;    send_json([        'id' => $newId,        'name' => $input['name'],        'email' => $input['email'],        'companyId' => $companyId,        'licenseType' => $licenseType,        'licenseExpiry' => $licenseExpiry    ]);} else {    send_error('Error adding user: ' . $conn->error, 500);}$conn->close();?>