<?phpinclude 'database_connection.php';check_login(); // <-- NEW: Security Checkif (empty($input['userIds']) || !is_array($input['userIds']) || empty($input['expiry'])) {    send_error('Missing user IDs or expiry date.');}$expiry = $conn->real_escape_string($input['expiry']);// Sanitize all user IDs to be integers$userIds = array_map('intval', $input['userIds']);$idList = implode(',', $userIds);$sql = "UPDATE users SET licenseExpiry = '$expiry' WHERE id IN ($idList)";if ($conn->query($sql) === TRUE) {    send_json(['success' => true, 'updated' => $conn->affected_rows]);} else {    send_error('Error updating expiry dates: ' . $conn->error, 500);}$conn->close();?>