<?php// Add these lines at the very top for debugging// Comment them out or remove them in productionini_set('display_errors', 1);ini_set('display_startup_errors', 1);error_reporting(E_ALL);// --- NEW: Start a session ---// This MUST be called before any outputsession_start();// --- Database Connection ---$servername = "localhost";$username = "yourcpaneluser_efd_api_user"; // <-- REPLACE$password = "your_db_password";           // <-- REPLACE$dbname = "yourcpaneluser_efd_license_manager"; // <-- REPLACE// Create connection$conn = new mysqli($servername, $username, $password, $dbname);// Check connectionif ($conn->connect_error) {  // Send a server error  http_response_code(500);  echo json_encode(['error' => 'Database connection failed: ' . $conn->connect_error]);  exit();}// Set charset to utf8mb4 for full UTF-8 support$conn->set_charset("utf8mb4");// Function to send a JSON responsefunction send_json($data) {  header('Content-Type: application/json');  echo json_encode($data);  exit();}// Function to handle errorsfunction send_error($message, $code = 400) {  http_response_code($code);  send_json(['error' => $message]);}// NEW: Function to check if user is authenticatedfunction check_login() {    if (!isset($_SESSION['loggedin']) || $_SESSION['loggedin'] !== true) {        send_error('Not logged in', 401); // 401 Unauthorized    }}// Get JSON input from the request body (for POST/PUT)$input = json_decode(file_get_contents('php://input'), true);?>