<?phpinclude 'database_connection.php';check_login(); // <-- Security Check$data = [    'companies' => [],    'users' => [],    'addons' => [],    'userAddons' => []];// 1. Get Companies - NOW INCLUDES onHold$result = $conn->query("SELECT id, name, onHold FROM companies");while($row = $result->fetch_assoc()) {    $row['id'] = (int)$row['id'];    $row['onHold'] = (bool)$row['onHold']; // Cast to boolean    $data['companies'][] = $row;}// 2. Get Users$result = $conn->query("SELECT * FROM users");while($row = $result->fetch_assoc()) {    $row['id'] = (int)$row['id'];    $row['companyId'] = (int)$row['companyId'];    $data['users'][] = $row;}// 3. Get Addons$result = $conn->query("SELECT * FROM addons");while($row = $result->fetch_assoc()) {    $row['id'] = (int)$row['id'];    $data['addons'][] = $row;}// 4. Get User Addon Assignments$result = $conn->query("SELECT * FROM user_addons");while($row = $result->fetch_assoc()) {    $row['id'] = (int)$row['id'];    $row['userId'] = (int)$row['userId'];    $row['addonId'] = (int)$row['addonId'];    $data['userAddons'][] = $row;}$conn->close();send_json($data);?>