<?phpinclude 'database_connection.php';check_login(); // <-- NEW: Security Checkif (empty($input['id'])) {    send_error('Addon ID is required.');}$addonId = (int)$input['id'];// The 'ON DELETE CASCADE' constraint on the user_addons table// will automatically delete all assignments.$sql = "DELETE FROM addons WHERE id = $addonId";if ($conn->query($sql) === TRUE) {    send_json(['success' => true, 'id' => $addonId]);} else {    send_error('Error removing addon: ' . $conn->error, 500);}$conn->close();?>